package oasys

import (
	"github.com/ethereum/go-ethereum/common"
	"github.com/ethereum/go-ethereum/params"
)

var (
	nftBridgeMainchain = &contract{
		name:    "NFTBridgeMainchain",
		address: "0x520000000000000000000000000000000000000e",
	}
	nftBridgeSidechain = &contract{
		name:    "NFTBridgeSidechain",
		address: "0x520000000000000000000000000000000000000f",
	}
	nftBridgeRelayer = &contract{
		name:    "NFTBridgeRelayer",
		address: "0x5200000000000000000000000000000000000010",
	}

	nftBridgeContractSet = &nftbridge{
		{
			contract: nftBridgeMainchain,
			// commit: https://github.com/oasysgames/oasys-genesis-contract/blob/382e8e2b879046aa2c4de09e27b5fc6c0d307873/contracts/nft-bridge/NFTBridgeMainchain.sol
			code:       "",
			storageMap: map[string]interface{}{},
		},
		{
			contract: nftBridgeSidechain,
			// commit: https://github.com/oasysgames/oasys-genesis-contract/blob/382e8e2b879046aa2c4de09e27b5fc6c0d307873/contracts/nft-bridge/NFTBridgeSidechain.sol
			code:       "",
			storageMap: map[string]interface{}{},
		},
		{
			contract: nftBridgeRelayer,
			// commit: https://github.com/oasysgames/oasys-genesis-contract/blob/382e8e2b879046aa2c4de09e27b5fc6c0d307873/contracts/nft-bridge/NFTBridgeRelayer.sol
			code: "",
			storageMap: map[string]interface{}{
				// address public mainchainBridge
				"0x02": nftBridgeMainchain.address,
				// address public sidechainBridge
				"0x03": nftBridgeSidechain.address,
			},
		},
	}
)

type nftbridge []*deployment

func (p *nftbridge) deploy(state StateDB, block uint64) {
	var owner common.Address

	switch GenesisHash {
	case params.OasysMainnetGenesisHash:
		owner = common.HexToAddress(mainnetGenesisWallet)
	case params.OasysTestnetGenesisHash:
		owner = common.HexToAddress(testnetGenesisWallet)
	}

	for _, c := range *p {
		cpy := c.copy()
		if (c.contract == nftBridgeMainchain || c.contract == nftBridgeSidechain) && owner != (common.Address{}) {
			// address private _owner
			cpy.storageMap["0x00"] = owner
		}
		cpy.deploy(state, block)
	}
}
